item.description:
&lt;%* tR += "---" %&gt;
tags:
create_time: &lt;% tp.file.creation_date("YYYY-MM-DD HH:mm") %&gt;
last_modify: &lt;% tp.file.last_modified_date("YYYY-MM-DD HH:mm") %&gt;

&lt;%*
// 定义一个数组，每个对象包含：显示文本（含图标和中文）和实际要插入的callout类型
let calloutOptions = [
{ display: "📝 Note (笔记)", value: "note" },
{ display: "🔮 Abstract (摘要)", value: "abstract" },
{ display: "ℹ️ Info (信息)", value: "info" },
{ display: "💡 Tip (提示)", value: "tip" },
{ display: "✅ Success (成功)", value: "success" },
{ display: "❓ Question (问题)", value: "question" },
{ display: "⚠️ Warning (警告)", value: "warning" },
{ display: "❌ Failure (失败)", value: "failure" },
{ display: "🚨 Danger (危险)", value: "danger" },
{ display: "🐛 Bug (虫害)", value: "bug" },
{ display: "📖 Example (示例)", value: "example" },
{ display: "💬 Quote (引用)", value: "quote" }
];// 从对象数组中提取 display 和 value 分别形成两个数组
let displayOptions = calloutOptions.map(option =&gt; option.display);
let valueOptions = calloutOptions.map(option =&gt; option.value);// 使用 suggester 显示带图标的选项，并获取用户选择
let chosenIndex = await tp.system.suggester(displayOptions, valueOptions, false, "请选择要插入的 Callout 类型");if (chosenIndex !== undefined) {
// 向笔记中插入 callout 语法，并将光标定位在 callout 内容开始处
tR += &gt; [!${chosenIndex}]\n&gt; + tp.file.cursor();
}
%&gt;
